select a.callID, a.UniqueCallId, a.ConferenceName, a.TenantName, a.ConferenceType, a.EndpointType, a.CallerID, a.CallerName, convert_tz(a.JoinTime, @@session.time_zone, '+00:00'), convert_tz(a.LeaveTime, @@session.time_zone, '+00:00'), a.CallState, a.Direction, b.Name as GWName, b.LOCAL_IP as GWIP, c.Name as RouterName, c.LOCAL_IP as RouterIP, a.GWPrefix, a.ApplicationName, a.ApplicationVersion, a.ApplicationOs, a.EndpointPublicIPAddress, a.CallCompletionCode, a.Extension, a.AccessType, a.RoomType, a.Roomowner from portal2.ConferenceCall2 a INNER JOIN portal2.components b ON a.GWID = b.COMP_ID INNER JOIN portal2.components c ON a.RouterID = c.COMP_ID where EndpointType = 'L' and a.CallState='IN PROGRESS' and a.JoinTime > %s  order by a.JoinTime
